    void loadAnh(JLabel lbl, String fileanh) {
        //TODO add your handling code here:
        ImageIcon icon = new ImageIcon(fileanh);
        Image image = icon.getImage().getScaledInstance(40, 40, Image.SCALE_SMOOTH);
        lbl.setIcon(new ImageIcon(image));
    }

    void loadNV() {
        ArrayList<TblDatCho> list;
        try {
            list = nvDL.getListNV();
            table = (DefaultTableModel) tblDatCho.getModel();
            for (TblDatCho nv : list) {
                table.addRow(new Object[]{nv.getMaDatCho(), nv.getTenChuong(), nv.getTenKhachHang(), nv.getSdt(), nv.getNgayDat(), nv.getNgaySD(), nv.getSoLuong()});
            }
        } catch (SQLException ex) {

        }
    }

    void loadmaChuong() {
        try {
            ArrayList<TblChuong> ch = cXLDL.getListNV("1");
            for (TblChuong c : ch) {
                cboMaChuong.addItem(c);
            }
        } catch (Exception ex) {

        }
    }

    private boolean KTDL() throws HeadlessException {
        StringBuilder sb = new StringBuilder();
        try {
            SimpleDateFormat dateFormat = new SimpleDateFormat("yyyy-MM-dd", Locale.getDefault());
            String ngaydat = dateFormat.format(jdcNgayDat.getDate());

        } catch (Exception e) {
            JOptionPane.showMessageDialog(this, "Vui lòng chọn đúng định dạng ngày cho ngày đặt");
            return false;
        }
        try {
            SimpleDateFormat dateFormat = new SimpleDateFormat("yyyy-MM-dd", Locale.getDefault());
            String ngaysd = dateFormat.format(jdcNgaySuDung.getDate());

        } catch (Exception e) {
            JOptionPane.showMessageDialog(this, "Vui lòng chọn đúng định dạng ngày cho ngày sử dụng");
            return false;
        }
        if (txtTenKhachHang.getText().equals("")) {
            sb.append("Vui lòng nhập tên khách hàng\n");
            txtTenKhachHang.requestFocus();
        }
        if (txtSoLuong.getText().equals("")) {
            sb.append("Vui lòng nhập số lượng\n");
            txtSoLuong.requestFocus();
        }
        if (txtSDT.getText().equals("")) {
            sb.append("Vui lòng nhập số điện thoại\n");
            txtSDT.requestFocus();
        }
        if (sb.length() > 0) {
            JOptionPane.showMessageDialog(this, sb.toString(), "Thông báo", JOptionPane.ERROR_MESSAGE);
            return false;
        } else {
            return true;
        }
    }

