    void hienthitxt(int i) {
        TableModel model = tblDatCho.getModel();
        txtMaSo.setText(model.getValueAt(i, 0).toString());
        String cbosub = model.getValueAt(i, 1).toString();
        for (int j = 0; j < cboMaChuong.getItemCount(); j++) {
            if (cboMaChuong.getItemAt(j).toString().equalsIgnoreCase(cbosub)) {
                cboMaChuong.setSelectedIndex(j);
            }
        }
        txtTenKhachHang.setText(model.getValueAt(i, 2).toString());
        txtSDT.setText(model.getValueAt(i, 3).toString());
        Date date = null;
        try {
            date = new SimpleDateFormat("yyyy-MM-dd").parse(model.getValueAt(i, 4).toString());
        } catch (ParseException ex) {
            Logger.getLogger(frmNhanVien.class.getName()).log(Level.SEVERE, null, ex);
        }
        jdcNgayDat.setDate(date);

        try {
            date = new SimpleDateFormat("yyyy-MM-dd").parse(model.getValueAt(i, 5).toString());
        } catch (ParseException ex) {
            Logger.getLogger(frmNhanVien.class.getName()).log(Level.SEVERE, null, ex);
        }
        jdcNgaySuDung.setDate(date);

        txtSoLuong.setText(model.getValueAt(i, 6).toString());
    }
    private void tblDatChoMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tblDatChoMouseClicked
        // TODO add your handling code here:
        int i = tblDatCho.getSelectedRow();
        hienthitxt(i);
    }//GEN-LAST:event_tblDatChoMouseClicked

    private void lblSearchMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_lblSearchMouseClicked
        // TODO add your handling code here:
        tblDatCho.removeAll();
        table.setRowCount(0);
        String tk = txtTimKiem.getText();
        ArrayList<TblDatCho> list;
        try {
            list = nvDL.getListDatCho(tk);
            table = (DefaultTableModel) tblDatCho.getModel();
            for (TblDatCho nv : list) {
                table.addRow(new Object[]{nv.getMaDatCho(), nv.getTenChuong(), nv.getTenKhachHang(), nv.getSdt(), nv.getNgayDat(), nv.getNgaySD(), nv.getSoLuong()});
            }
        } catch (SQLException ex) {

        }
    }//GEN-LAST:event_lblSearchMouseClicked


