    void loadAnh(JLabel lbl, String fileanh) {
        //TODO add your handling code here:
        ImageIcon icon = new ImageIcon(fileanh);
        Image image = icon.getImage().getScaledInstance(40, 40, Image.SCALE_SMOOTH);
        lbl.setIcon(new ImageIcon(image));
    }

    void loadNV() {
        ArrayList<TblHinhThuc> list;
        try {
            list = nvDL.getListNV();
            table = (DefaultTableModel) tblHinhThuc.getModel();
            for (TblHinhThuc nv : list) {
                table.addRow(new Object[]{nv.getMaHinhThuc(), nv.getLoai(), nv.getGia()});
            }
        } catch (SQLException ex) {

        }
    }

    void hienthitxt(int i) {
        TableModel model = tblHinhThuc.getModel();
        txtMaHinhThuc.setText(model.getValueAt(i, 0).toString());
        txtLoai.setText(model.getValueAt(i, 1).toString());
        txtGia.setText(model.getValueAt(i, 2).toString());
    }

    private boolean KTDL() throws HeadlessException {
        StringBuilder sb = new StringBuilder();
        if (txtLoai.getText().equals("")) {
            sb.append("Vui lòng nhập loại\n");
            txtLoai.requestFocus();
        }
        if (txtGia.getText().equals("")) {
            sb.append("Vui lòng nhập giá\n");
            txtGia.requestFocus();
        }
        if (sb.length() > 0) {
            JOptionPane.showMessageDialog(this, sb.toString(), "Thông báo", JOptionPane.ERROR_MESSAGE);
            return false;
        } else {
            return true;
        }
    }

