    private void lblSearchMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_lblSearchMouseClicked
        // TODO add your handling code here:
        tblHinhThuc.removeAll();
        table.setRowCount(0);

        String tk = txtTimKiem.getText();
        ArrayList<TblHinhThuc> list;
        try {
            list = nvDL.getListHinhThuc(tk);
            table = (DefaultTableModel) tblHinhThuc.getModel();
            for (TblHinhThuc nv : list) {
                table.addRow(new Object[]{nv.getMaHinhThuc(), nv.getLoai(), nv.getGia()});
            }
        } catch (SQLException ex) {

        }
    }//GEN-LAST:event_lblSearchMouseClicked


