    void loadAnh(JLabel lbl, String fileanh) {
        //TODO add your handling code here:
        ImageIcon icon = new ImageIcon(fileanh);
        Image image = icon.getImage().getScaledInstance(40, 40, Image.SCALE_SMOOTH);
        lbl.setIcon(new ImageIcon(image));
    }

    void loadNV() {
        ArrayList<TblKhachHang> list;
        try {
            list = nvDL.getListNV();
            table = (DefaultTableModel) tblKhachHang.getModel();
            for (TblKhachHang nv : list) {
                table.addRow(new Object[]{nv.getMaKH(), nv.getTenKH(), nv.getDiaChi(), nv.getSdt(), nv.getLoai()});
            }
        } catch (SQLException ex) {

        }
    }

    void hienthitxt(int i) {
        TableModel model = tblKhachHang.getModel();
        txtMaKhachHang.setText(model.getValueAt(i, 0).toString());
        txtHoTen.setText(model.getValueAt(i, 1).toString());
        txtDiaChi.setText(model.getValueAt(i, 2).toString());
        txtSDT.setText(model.getValueAt(i, 3).toString());
        txtLoaiKhachHang.setText(model.getValueAt(i, 4).toString());
    }

    private boolean KTDL() throws HeadlessException {
        StringBuilder sb = new StringBuilder();

        if (txtHoTen.getText().equals("")) {
            sb.append("Vui lòng nhập họ tên\n");
            txtHoTen.requestFocus();
        }
        if (txtLoaiKhachHang.getText().equals("")) {
            sb.append("Vui lòng nhập loại khách hàng\n");
            txtLoaiKhachHang.requestFocus();
        }
        if (txtDiaChi.getText().equals("")) {
            sb.append("Vui lòng nhập địa chỉ\n");
            txtDiaChi.requestFocus();
        }
        if (txtSDT.getText().equals("")) {
            sb.append("Vui lòng nhập số điện thoại\n");
            txtSDT.requestFocus();
        }
        if (sb.length() > 0) {
            JOptionPane.showMessageDialog(this, sb.toString(), "Thông báo", JOptionPane.ERROR_MESSAGE);
            return false;
        } else {
            return true;
        }
    }

