    private void btnLuuActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnLuuActionPerformed
        // TODO add your handling code here:
        if (them) {
            if (KTDL()) {
                int sdt = Integer.parseInt(txtSDT.getText());
                try {
                    Connection conn = dao.getConnect();
                    String caulenh = "INSERT INTO tbl_KhachHang VALUES(N'" + txtHoTen.getText() + "',N'" + txtDiaChi.getText() + "','" + sdt + "',N'" + txtLoaiKhachHang.getText() + "')";
                    boolean a = dao.Lenh(caulenh, conn);
                    if (a) {
                        tblKhachHang.removeAll();
                        table.setRowCount(0);
                        loadNV();
                        JOptionPane.showMessageDialog(this, "Thêm thành công");
                    } else {
                        JOptionPane.showMessageDialog(this, "Thêm KHÔNG thành công");
                    }
                } catch (SQLException ex) {
                    Logger.getLogger(frmKhachHang.class.getName()).log(Level.SEVERE, null, ex);
                }
            }
        }
        if (sua) {
            int sdt = Integer.parseInt(txtSDT.getText());
            try {
                Connection conn = dao.getConnect();
                String caulenh = "UPDATE tbl_KhachHang SET TenKH=N'" + txtHoTen.getText() + "',DiaChi=N'" + txtDiaChi.getText() + "',SDT='" + sdt + "',Loai=N'" + txtLoaiKhachHang.getText() + "'where MaKH=" + txtMaKhachHang.getText();
                boolean a = dao.Lenh(caulenh, conn);
                if (a) {
                    tblKhachHang.removeAll();
                    table.setRowCount(0);
                    loadNV();
                    JOptionPane.showMessageDialog(this, "Sửa thành công");
                } else {
                    JOptionPane.showMessageDialog(this, "Sửa KHÔNG thành công");
                }
            } catch (SQLException ex) {
                Logger.getLogger(frmKhachHang.class.getName()).log(Level.SEVERE, null, ex);
            }
        }
        if (xoa) {
            try {
                Connection conn = dao.getConnect();
                String caulenh = "DELETE FROM tbl_KhachHang WHERE MaKH=" + txtMaKhachHang.getText();
                boolean a = dao.Lenh(caulenh, conn);
                if (a) {
                    tblKhachHang.removeAll();
                    table.setRowCount(0);
                    loadNV();
                    JOptionPane.showMessageDialog(this, "Xóa thành công");
                } else {
                    JOptionPane.showMessageDialog(this, "Xóa KHÔNG thành công");
                }
            } catch (SQLException ex) {
                Logger.getLogger(frmKhachHang.class.getName()).log(Level.SEVERE, null, ex);
            }
        }
    }//GEN-LAST:event_btnLuuActionPerformed

    private void lblSearchMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_lblSearchMouseClicked
        // TODO add your handling code here:
        tblKhachHang.removeAll();
        table.setRowCount(0);
        String tk = txtTimKiem.getText();
        ArrayList<TblKhachHang> list;
        try {
            list = nvDL.getListNhanVien(tk);
            table = (DefaultTableModel) tblKhachHang.getModel();
            for (TblKhachHang nv : list) {
                table.addRow(new Object[]{nv.getMaKH(), nv.getTenKH(), nv.getDiaChi(), nv.getSdt(), nv.getLoai()});
            }
        } catch (SQLException ex) {

        }
    }//GEN-LAST:event_lblSearchMouseClicked


