    void loadNV() {
        ArrayList<TblThuCungKyGui> list;
        try {
            list = nvDL.getListNV();
            table = (DefaultTableModel) tblKyGui.getModel();
            for (TblThuCungKyGui nv : list) {
                table.addRow(new Object[]{nv.getMaSoThuCung(), nv.getGiong(), nv.getLoai(), nv.getCanNang(), nv.getTinhTrangSK(), nv.getTenKH(), nv.getHinhAnh(),nv.getTrangThai()});
            }
        } catch (SQLException ex) {

        }
    }

    void loadAnh(JLabel lbl, String fileanh) {
        ImageIcon icon = new ImageIcon(fileanh);
        Image image = icon.getImage().getScaledInstance(lbl.getWidth(), lbl.getHeight(), Image.SCALE_SMOOTH);
        lbl.setIcon(new ImageIcon(image));
    }

    void loadhinh(JLabel lbl, String fileanh) {
        ImageIcon icon = new ImageIcon(fileanh);
        Image image = icon.getImage().getScaledInstance(40, 40, Image.SCALE_SMOOTH);
        lbl.setIcon(new ImageIcon(image));
    }

    void loadmaKH() {
        try {
            ArrayList<TblKhachHang> KH = khDL.getListNV();
            for (TblKhachHang kh : KH) {
                cboMaKhachHang.addItem(kh);
            }
        } catch (Exception ex) {

        }
    }

    private boolean KTDL() throws HeadlessException {
        StringBuilder sb = new StringBuilder();

        if (tenFile == "") {
            sb.append("Vui lòng chọn file ảnh\n");
        }
        if (txtGiong.getText().equals("")) {
            sb.append("Vui lòng nhập giống\n");
            txtGiong.requestFocus();
        }
        if (txtCanNang.getText().equals("")) {
            sb.append("Vui lòng nhập cân nặng\n");
            txtCanNang.requestFocus();
        }
        if (txtLoai.getText().equals("")) {
            sb.append("Vui lòng nhập loại\n");
            txtLoai.requestFocus();
        }
        if (txtTinhTrangSK.getText().equals("")) {
            sb.append("Vui lòng nhập tình trạng sức khỏe\n");
            txtTinhTrangSK.requestFocus();
        }

        if (sb.length() > 0) {
            JOptionPane.showMessageDialog(this, sb.toString(), "Thông báo", JOptionPane.ERROR_MESSAGE);
            return false;
        } else {
            return true;
        }
    }

