    private void btnLuuActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnLuuActionPerformed
        // TODO add your handling code here:
        if (them) {
            if (KTDL()) {
                TblKhachHang kh = (TblKhachHang) cboMaKhachHang.getSelectedItem();
                String makhachhang = String.valueOf(kh.getMaKH());
                String hinhanh = tenFile;
                String trangthai=cboTrangThai.getSelectedItem().toString();

                try {
                    Connection conn = dao.getConnect();
                    String caulenh = "INSERT INTO tbl_ThuCungKyGui VALUES(N'" + txtGiong.getText() + "',N'" + txtLoai.getText() + "','" + txtCanNang.getText() + "',N'" + txtTinhTrangSK.getText() + "','" + makhachhang + "','" + tenFile + "',N'" + trangthai + "')";
                    boolean a = dao.Lenh(caulenh, conn);
                    if (a) {
                        tblKyGui.removeAll();
                        table.setRowCount(0);
                        loadNV();
                        JOptionPane.showMessageDialog(this, "Thêm thành công");
                    } else {
                        JOptionPane.showMessageDialog(this, "Thêm KHÔNG thành công");
                    }
                } catch (SQLException ex) {
                    Logger.getLogger(frmKyGui.class.getName()).log(Level.SEVERE, null, ex);
                }
            }
        }
        if (sua) {
            int mathucung = Integer.parseInt(txtMaThuCung.getText());
            TblKhachHang kh = (TblKhachHang) cboMaKhachHang.getSelectedItem();
            String makhachhang = String.valueOf(kh.getMaKH());
            String hinhanh = tenFile;
            String trangthai=cboTrangThai.getSelectedItem().toString();
            String caulenh ="";
            if(hinhanh!=null && !hinhanh.equals("")){
                caulenh = "UPDATE tbl_ThuCungKyGui SET Giong=N'" + txtGiong.getText() + "',Loai=N'" + txtLoai.getText() + "',CanNang='" + txtCanNang.getText() + "',TinhTrangSK=N'" + txtTinhTrangSK.getText() + "',MaKH='" + makhachhang + "',HinhAnh='" + tenFile + "',TrangThai=N'" + trangthai + "'where MaSoThuCung=" + mathucung;
            }else{
                caulenh = "UPDATE tbl_ThuCungKyGui SET Giong=N'" + txtGiong.getText() + "',Loai=N'" + txtLoai.getText() + "',CanNang='" + txtCanNang.getText() + "',TinhTrangSK=N'" + txtTinhTrangSK.getText() + "',MaKH='" + makhachhang + "',TrangThai=N'" + trangthai + "'where MaSoThuCung=" + mathucung;
            }
            try {
                Connection conn = dao.getConnect();
                boolean a = dao.Lenh(caulenh, conn);
                if (a) {
                    tblKyGui.removeAll();
                    table.setRowCount(0);
                    loadNV();
                    JOptionPane.showMessageDialog(this, "Sửa thành công");
                } else {
                    JOptionPane.showMessageDialog(this, "Sửa KHÔNG thành công");
                }
            } catch (SQLException ex) {
                Logger.getLogger(frmKyGui.class.getName()).log(Level.SEVERE, null, ex);
            }
        }
        if (xoa) {
            int mathucung = Integer.parseInt(txtMaThuCung.getText());
            try {
                Connection conn = dao.getConnect();
                String caulenh = "DELETE FROM tbl_ThuCungKyGui WHERE MaSoThuCung=" + mathucung;
                boolean a = dao.Lenh(caulenh, conn);
                if (a) {
                    tblKyGui.removeAll();
                    table.setRowCount(0);
                    loadNV();
                    JOptionPane.showMessageDialog(this, "Xóa thành công");
                } else {
                    JOptionPane.showMessageDialog(this, "Xóa KHÔNG thành công");
                }
            } catch (SQLException ex) {
                Logger.getLogger(frmKyGui.class.getName()).log(Level.SEVERE, null, ex);
            }
        }
    }//GEN-LAST:event_btnLuuActionPerformed

