    void hienthitxt(int i) {
        TableModel model = tblKyGui.getModel();
        txtMaThuCung.setText(model.getValueAt(i, 0).toString());
        txtGiong.setText(model.getValueAt(i, 1).toString());
        txtLoai.setText(model.getValueAt(i, 2).toString());
        txtCanNang.setText(model.getValueAt(i, 3).toString());
        txtTinhTrangSK.setText(model.getValueAt(i, 4).toString());
        String cbosub = model.getValueAt(i, 5).toString();
        for (int j = 0; j < cboMaKhachHang.getItemCount(); j++) {
            if (cboMaKhachHang.getItemAt(j).toString().equalsIgnoreCase(cbosub)) {
                cboMaKhachHang.setSelectedIndex(j);
            }
        }
        String src = "src/img/" + model.getValueAt(i, 6).toString();
        loadAnh(lblHinhAnh, src);
        cboTrangThai.setSelectedItem(model.getValueAt(i, 7).toString());
    }
    private void rSButtonMetro1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_rSButtonMetro1ActionPerformed
        // TODO add your handling code here:
        JFileChooser chooser = new JFileChooser();
        chooser.showOpenDialog(null);
        File f = chooser.getSelectedFile();

        loadAnh(lblHinhAnh, f.toString());
        String fileName = f.getAbsolutePath();
        String newPath = "src/img/";
        File diretory = new File(newPath);
        if (!diretory.exists()) {
            diretory.mkdirs();
        }
        File sourceFile = null;
        File destinationFile = null;
        String extension = fileName.substring(fileName.lastIndexOf('\\') + 1);
        sourceFile = new File(fileName);
        destinationFile = new File(newPath + extension);
        tenFile = extension;
        try {
            Files.copy(sourceFile.toPath(), destinationFile.toPath());
        } catch (IOException ex) {

        }
    }//GEN-LAST:event_rSButtonMetro1ActionPerformed

    private void lblSearchMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_lblSearchMouseClicked
        // TODO add your handling code here:
        tblKyGui.removeAll();
        table.setRowCount(0);
        String tk = txtTimKiem.getText();
        ArrayList<TblThuCungKyGui> list;
        try {
            list = nvDL.getListKyGui(tk);
            table = (DefaultTableModel) tblKyGui.getModel();
            for (TblThuCungKyGui nv : list) {
                table.addRow(new Object[]{nv.getMaSoThuCung(), nv.getGiong(), nv.getLoai(), nv.getCanNang(), nv.getTinhTrangSK(), nv.getTenKH(), nv.getHinhAnh(),nv.getTrangThai()});
            }
        } catch (SQLException ex) {

        }
    }//GEN-LAST:event_lblSearchMouseClicked


