    void loadAnh(JLabel lbl, String fileanh) {
        //TODO add your handling code here:
        ImageIcon icon = new ImageIcon(fileanh);
        Image image = icon.getImage().getScaledInstance(40, 40, Image.SCALE_SMOOTH);
        lbl.setIcon(new ImageIcon(image));
    }

    void loadNV() {
        ArrayList<TblNhatKyNuoi> list;
        try {
            list = nvDL.getListNV();
            table = (DefaultTableModel) tblNhatKy.getModel();
            for (TblNhatKyNuoi nv : list) {
                table.addRow(new Object[]{nv.getMaNhatKy(), nv.getGiong(), nv.getNgay(), nv.getBuoi(), nv.getTrangThai(), nv.getGhiChu(), nv.getDichVu(), nv.getThanhTien()});
            }
        } catch (SQLException ex) {

        }
    }

    void loadmaKyGuiThuCung() {
        try {
            ArrayList<TblThuCungKyGui> ch = kgXLDL.getListNV();
            for (TblThuCungKyGui c : ch) {
                cboMaThuCung.addItem(c);
            }
        } catch (Exception ex) {

        }
    }
    void loaddv() {
        try {
            ArrayList<TblDichVu> ch = dvDL.getListNV();
            for (TblDichVu c : ch) {
                cboDichVu.addItem(c);
            }
        } catch (Exception ex) {

        }
    }

    private boolean KTDL() throws HeadlessException {
        StringBuilder sb = new StringBuilder();
        try {
            SimpleDateFormat dateFormat = new SimpleDateFormat("yyyy-MM-dd", Locale.getDefault());
            String ngay = dateFormat.format(jdcNgay.getDate());
            
        } catch (Exception e) {
            JOptionPane.showMessageDialog(this, "Vui lòng chọn đúng định dạng ngày");
            return false;
        }
        
        if (txtBuoi.getText().equals("")) {
            sb.append("Vui lòng nhập buổi\n");
            txtBuoi.requestFocus();
        }
        if (txtTrangThai.getText().equals("")) {
            sb.append("Vui lòng nhập trạng thái\n");
            txtTrangThai.requestFocus();
        }
        if (sb.length() > 0) {
            JOptionPane.showMessageDialog(this, sb.toString(), "Thông báo", JOptionPane.ERROR_MESSAGE);
            return false;
        } else {
            return true;
        }
    }

