    private void btnLuuActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnLuuActionPerformed
        // TODO add your handling code here:
        if (them) {
            if (KTDL()) {
                SimpleDateFormat dateFormat = new SimpleDateFormat("yyyy-MM-dd", Locale.getDefault());

                TblThuCungKyGui kh = (TblThuCungKyGui) cboMaThuCung.getSelectedItem();
                String mathucung = String.valueOf(kh.getMaSoThuCung());
                String dichvu = cboDichVu.getSelectedItem().toString();

                String ngay = dateFormat.format(jdcNgay.getDate());

                try {
                    Connection conn = dao.getConnect();
                    String caulenh = "INSERT INTO tbl_NhatKyNuoi VALUES('" + mathucung + "','" + ngay + "',N'" + txtBuoi.getText() + "',N'" + txtTrangThai.getText() + "',N'" + txtGhiChu.getText() + "',N'" + dichvu + "','" + txtThanhTien.getText() + "')";
                    boolean a = dao.Lenh(caulenh, conn);
                    if (a) {
                        tblNhatKy.removeAll();
                        table.setRowCount(0);
                        loadNV();
                        JOptionPane.showMessageDialog(this, "Thêm thành công");
                    } else {
                        JOptionPane.showMessageDialog(this, "Thêm KHÔNG thành công");
                    }
                } catch (SQLException ex) {
                    Logger.getLogger(frmNhatKy.class.getName()).log(Level.SEVERE, null, ex);
                }
            }
        }
        if (sua) {
            SimpleDateFormat dateFormat = new SimpleDateFormat("yyyy-MM-dd", Locale.getDefault());
            int manhatky = Integer.parseInt(txtMaNhatKy.getText());

            TblThuCungKyGui kh = (TblThuCungKyGui) cboMaThuCung.getSelectedItem();
                String mathucung = String.valueOf(kh.getMaSoThuCung());
            String dichvu = cboDichVu.getSelectedItem().toString();
            String ngay = dateFormat.format(jdcNgay.getDate());

            try {
                Connection conn = dao.getConnect();
                String caulenh = "UPDATE tbl_NhatKyNuoi SET MaSoThuCung='" + mathucung + "',Ngay='" + ngay + "',Buoi=N'" + txtBuoi.getText() + "',TrangThai=N'" + txtTrangThai.getText() + "',GhiChu=N'" + txtGhiChu.getText() + "',DichVu=N'" + dichvu + "',ThanhTien='" + txtThanhTien.getText() + "'where MaNhatKy=" + manhatky;
                boolean a = dao.Lenh(caulenh, conn);
                if (a) {
                    tblNhatKy.removeAll();
                    table.setRowCount(0);
                    loadNV();
                    JOptionPane.showMessageDialog(this, "Sửa thành công");
                } else {
                    JOptionPane.showMessageDialog(this, "Sửa KHÔNG thành công");
                }
            } catch (SQLException ex) {
                Logger.getLogger(frmNhatKy.class.getName()).log(Level.SEVERE, null, ex);
            }
        }
        if (xoa) {
            int manhatky = Integer.parseInt(txtMaNhatKy.getText());
            try {
                Connection conn = dao.getConnect();
                String caulenh = "DELETE FROM tbl_NhatKyNuoi WHERE MaNhatKy=" + manhatky;
                boolean a = dao.Lenh(caulenh, conn);
                if (a) {
                    tblNhatKy.removeAll();
                    table.setRowCount(0);
                    loadNV();
                    JOptionPane.showMessageDialog(this, "Xóa thành công");
                } else {
                    JOptionPane.showMessageDialog(this, "Xóa KHÔNG thành công");
                }
            } catch (SQLException ex) {
                Logger.getLogger(frmNhatKy.class.getName()).log(Level.SEVERE, null, ex);
            }
        }
    }//GEN-LAST:event_btnLuuActionPerformed

