    void hienthitxt(int i) {
        TableModel model = tblNhatKy.getModel();
        txtMaNhatKy.setText(model.getValueAt(i, 0).toString());
        String cbosub = model.getValueAt(i, 1).toString();
        for (int j = 0; j < cboMaThuCung.getItemCount(); j++) {
            if (cboMaThuCung.getItemAt(j).toString().equalsIgnoreCase(cbosub)) {
                cboMaThuCung.setSelectedIndex(j);
            }
        }
        Date date = null;
        try {
            date = new SimpleDateFormat("yyyy-MM-dd").parse(model.getValueAt(i, 2).toString());
        } catch (ParseException ex) {
            Logger.getLogger(frmNhanVien.class.getName()).log(Level.SEVERE, null, ex);
        }
        jdcNgay.setDate(date);
        txtBuoi.setText(model.getValueAt(i, 3).toString());
        
        txtTrangThai.setText(model.getValueAt(i, 4).toString());
        txtGhiChu.setText(model.getValueAt(i, 5).toString());
        String cbosub1 = model.getValueAt(i, 6).toString();
        for (int j = 0; j < cboDichVu.getItemCount(); j++) {
            if (cboDichVu.getItemAt(j).toString().equalsIgnoreCase(cbosub1)) {
                cboDichVu.setSelectedIndex(j);
            }
        }
        txtThanhTien.setText(model.getValueAt(i, 7).toString());
    }
