    private void lblSearchMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_lblSearchMouseClicked
        // TODO add your handling code here:
        tblNhatKy.removeAll();
        table.setRowCount(0);
        String tk = txtTimKiem.getText();
        ArrayList<TblNhatKyNuoi> list;
        try {
            list = nvDL.getListNhatKy(tk);
            table = (DefaultTableModel) tblNhatKy.getModel();
            for (TblNhatKyNuoi nv : list) {
                table.addRow(new Object[]{nv.getMaNhatKy(), nv.getGiong(), nv.getNgay(), nv.getBuoi(), nv.getTrangThai(), nv.getGhiChu(), nv.getDichVu(), nv.getThanhTien()});
            }
        } catch (SQLException ex) {

        }
    }//GEN-LAST:event_lblSearchMouseClicked

    private void cboDichVuItemStateChanged(java.awt.event.ItemEvent evt) {//GEN-FIRST:event_cboDichVuItemStateChanged
        // TODO add your handling code here:
        if(evt.getStateChange() == ItemEvent.SELECTED){
           TblDichVu ch = (TblDichVu) cboDichVu.getSelectedItem();
            long madv = ch.getMaDV();
            try {
                TblDichVu dv= dvDL.getDichVu(madv);
                lblGia.setText(dv.getGia().toString());
                txtThanhTien.setText(dv.getGia().toString());
            } catch (SQLException ex) {
                Logger.getLogger(frmNhatKy.class.getName()).log(Level.SEVERE, null, ex);
            }
        }
    }//GEN-LAST:event_cboDichVuItemStateChanged

